sql.where:
p.id = b.providerId

  and  productName like CONCAT('%',#{bill.productName},'%')


  and  isPayment = #{bill.isPayment}


  and  p.id=#{bill.providerId}


  and b.providerId = #{bill.providerId,jdbcType=BIGINT}

mapper.select:
SELECT b.id,b.billCode,b.productName,b.isPayment,p.id,p.proname,p.proCode,b.`productCount`,b.`isPayment`,b.`creationDate`,`providerId`,b.totalPrice,b.productUnit from smbms_bill b, smbms_provider p
p.id=b.providerId
and b.id = #{id,jdbcType=BIGINT}
